// 액션 선언
export const FOLLOWWING_COUNT_VALUE_STATED = 'DO_FOLLOWWING_COUNT_VALUE';
export const FOLLOWER_COUNT_VALUE_STATED = 'DO_FOLLOWER_COUNT_VALUE_STATED';

// 사가 액션 선언
export const FOLLOWWING_COUNT_VALUE = 'DO_FOLLOWWING_COUNT_VALUE';
export const FOLLOWER_COUNT_VALUE = 'FOLLOWER_COUNT_VALUE';
// 초기 값
export const initialState = {
  following: 0,
  follower: 0,
  followingList: [],
  followerList: [],
};
// 액션
const followReducer = (state = initialState, action) => {
  switch (action.type) {
    case FOLLOWWING_COUNT_VALUE_STATED: {
      return { ...state };
    }
    case FOLLOWER_COUNT_VALUE_STATED: {
      return { ...state };
    }
    default: {
      return { ...state };
    }
  }
};

export default followReducer;
