import React, {
  Fragment, useState, useEffect, memo,
} from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { FETCH_FOLLOW_COUNT_VALUE } from '../../redux/reducers/followReducer';

const Profile = memo(() => {
  const login = useSelector((state) => state.sagaLogin, []);
  const follow = useSelector((state) => state.follow, []);
  const [flwing, setFlwing] = useState();
  const [flwer, setFlwer] = useState();
  const dispatch = useDispatch();
  useEffect(() => {
    // console.log(follow)
    const fetchData = async () => {
      await dispatch({ type: FETCH_FOLLOW_COUNT_VALUE, payload: login.username });
      setFlwing(follow.following);
      setFlwer(follow.follower);
    };
    fetchData();
  }, [flwing, flwer]);
  return (
    <Fragment>
      <div className="right_content card">
        <p>팔로우</p>
        <p>{flwing}</p>
        <p>팔로잉</p>
        <p>{flwer}</p>
      </div>
      <style jsx>
        {`
          .right_content{
            float:left;
            width:30%;
            height:500px;
            background-color:white;
          }
        `}
      </style>
    </Fragment>
  );
});

export default Profile;
