/* eslint-disable no-nested-ternary */
/* eslint-disable no-unused-expressions */
import React, { Fragment, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { FETCH_CARDS_START, LIKE_CARDS_REQUEST, UNLIKE_CARDS_REQUEST } from '../../redux/reducers/cardReducer';

const Card = () => {
  const login = useSelector((state) => state.sagaLogin, []);
  const { fetchState, card } = useSelector((state) => state.card, []);
  const dispatch = useDispatch();
  useEffect(() => {
    const fetchData = () => {
      dispatch({ type: FETCH_CARDS_START, payload: login.username });
    };
    fetchData();
  }, []);
  // console.log(card);
  const clickLike = (v) => {
    console.log('like', login.username, v.board_id);
    const payload = {
      username: login.username,
      board_id: v.board_id,
    };
    dispatch({ type: LIKE_CARDS_REQUEST, payload });
  };
  const clickUnLike = (v) => {
    console.log('unlike', login.username, v.board_id);
    const payload = {
      username: login.username,
      board_id: v.board_id,
    };
    dispatch({ type: UNLIKE_CARDS_REQUEST, payload });
  };
  return (
    <Fragment>
      <div className="left_content">
        {card.map((v, i) => (
          <div className="card main_card" key={i}>
            <div>
              <span>{v.user_id}</span>
            </div>
            <div>
              <span>{v.title}</span>
            </div>
            <div>
              {v.desc}
            </div>
            <div className="like">
              {
                ((username) => {
                  const filter = v.likes.findIndex((e) => e.user_id === username);
                  if (filter === -1) {
                    return <i className="fa fa-heart-o" aria-hidden="true" onClick={() => clickLike(v)} />;
                  }
                  return <i className="fa fa-heart" aria-hidden="true" onClick={() => clickUnLike(v)} />;
                })(login.username)
              }
              {' '}
              <span>
                {
                  v.likes.length === 0 ? 0
                    : v.likes.length === 1 ? `${v.likes[0].user_id}님이 좋아합니다.`
                      : `${v.likes[0].user_id}님 외 ${v.likes.length - 1}명이 좋아합니다.`
                }
              </span>
            </div>
            <div>
              <input type="text" className="reply_input" placeholder="댓글" />
              <input type="button" className="reply_btn" value="입력" />
            </div>
            <div>
              {
                (() => {
                  console.log(v.replies[0].desc);
                  v.replies.map((e, vi) => <div key={i}>{v.replies[vi].desc}</div>);
                })()
              }
            </div>
          </div>
        ))}
      </div>
      <style jsx>
        {`
          .left_content{
            float:left;
            width:69%;
            margin-right: 1%;
          }
          .main_card{
            margin-bottom:10px;
            padding:10px;
          }
          .reply_input{
            border:1px solid rgba(0,0,0,.125);
            width:90%;
            border-radius:5px;
            padding-left:10px;
            height:30px;
          }
          .reply_btn{
            border:1px solid rgba(0,0,0,.125);
            width:10%;
            height:30px;
            border-radius:5px;
          }
          .like{
            margin:5px 0;
          }
          .like i{
            color:red;
            font-weight:700;
            font-size:13px;
            cursor:pointer;
          }
          .like span{
            font-size:12px;
          }
        `}
      </style>
    </Fragment>
  );
};

export default Card;
