import { all } from 'redux-saga/effects';
import {
  watchSetUsername, watchSetUserpassword,
  watchLogin, watchSession,
} from './login';
import watchFollow from './follow';
import {
  watchFetchCard, watchLikeCard,
  watchUnLikeCard, watchAddReply,
  watchDelReply,
} from './card';

// all 함수를 통해 Saga들을 하나로 묶어줄수 있다.
export default function* rootSaga() {
  yield all([
    watchSetUsername(),
    watchSetUserpassword(),
    watchLogin(),
    watchSession(),
    watchFollow(),
    watchFetchCard(),
    watchLikeCard(),
    watchUnLikeCard(),
    watchAddReply(),
    watchDelReply(),
  ]);
}
