/* eslint-disable camelcase */
export const FETCH_CARDS_START = 'FETCH_CARDS_START';
export const FETCH_CARDS_SUCCESS = 'FETCH_CARDS_SUCCESS';
export const FETCH_CARDS_FAIL = 'FETCH_CARDS_FAIL';

export const initialState = {
  fetchState: '',
  card: [],
};

const cardReducer = (state = initialState, action) => {
  switch (action.type) {
    case FETCH_CARDS_START: {
      return { ...state, fetchState: 'card_fetching' };
    }
    case FETCH_CARDS_SUCCESS: {
      return { ...state, fetchState: 'card_fetching_success' };
    }
    case FETCH_CARDS_FAIL: {
      return { ...state, fetchState: 'card_fetching_fail' };
    }
    default: {
      return state;
    }
  }
};

export default cardReducer;
