/* eslint-disable no-shadow */
/* eslint-disable react/jsx-props-no-spreading */
import React, { Fragment, useEffect } from 'react';
// import App from 'next/app';
import axios from 'axios';
import { Provider, useDispatch, useSelector } from 'react-redux';
import withRedux from 'next-redux-wrapper';
import Head from 'next/head';
import { ServerStyleSheets, ThemeProvider } from '@material-ui/core/styles';
import moment from 'moment';
import Layout from '../components/Layout';
import 'bootstrap/dist/css/bootstrap.min.css';
import store from '../redux/store';

moment.locale('ko');

const RootApp = (props) => {
  const { Component, pageProps, store } = props;

  useEffect(() => {
    const jssStyles = document.querySelector('#jss-server-side');
    if (jssStyles) {
      jssStyles.parentNode.removeChild(jssStyles);
    }
  });

  return (
    <Fragment>
      <Head>
        <title>Devsol Blog</title>
      </Head>
      <Provider store={store}>
        <Layout>
          <Component {...pageProps} />
        </Layout>
      </Provider>
    </Fragment>
  );
};

RootApp.getInitialProps = async (context) => {
  // console.log(context.Component.getInitialProps);
  // console.log(context.ctx);
  const { Component, ctx } = context;
  console.log(ctx.store.getState());

  const res = await axios.get('http://127.0.0.1:3001/auth/session_check', {
    withCredentials: true, // 쿠키를 주고받을 수 있게됨
  });
  // console.log(res.data.session_data);
  const user = res.data.session_data;
  const pageProps = Component.getInitialProps ? await Component.getInitialProps(ctx) : {};

  return { pageProps };
};

export default withRedux(() => (store))(RootApp);
// export default RootApp;
