import React, {
  Fragment, useState, useEffect, memo,
} from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { FETCH_FOLLOW_COUNT_VALUE } from '../../redux/reducers/followReducer';

const Profile = memo(() => {
  const login = useSelector((state) => state.sagaLogin, []);
  const { following, follower } = useSelector((state) => state.follow, []);
  const dispatch = useDispatch();
  useEffect(() => {
    // console.log(follow)
    const fetchData = async () => {
      dispatch({ type: FETCH_FOLLOW_COUNT_VALUE, payload: login.username });
    };
    fetchData();
  }, [following, follower]);

  return (
    <Fragment>
      <div className="right_content card">
        <p className="p_name">
          {`${login.name} (${login.username})`}
        </p>
        <table>
          <thead>
            <tr>
              <th>Follower</th>
              <th>Following</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td>{follower}</td>
              <td>{following}</td>
            </tr>
          </tbody>
        </table>
      </div>
      <style jsx>
        {`
          .right_content{
            float:left;
            width:30%;
            height:500px;
            background-color:white;
          }
          
          .p_name{
            text-align:center;
            margin:10px auto;
            font-size:20px;
          }

          table{
            width:80%;
            margin:0 auto;  
            text-align:center;
          }
        `}
      </style>
    </Fragment>
  );
});

export default Profile;
