import { combineReducers } from 'redux';

export const SAGA_UP = 'SAGA_UP';
export const SAGA_DOWN = 'SAGA_DOWN';
export const SAGA_UP_ASYNC = 'SAGA_UP_ASYNC';
export const SAGA_DOWN_ASYNC = 'SAGA_DOWN_ASYNC';

export const THUNK_UP_COUNTER = 'THUNK_UP_COUNTER';
export const THUNK_DOWN_COUNTER = 'THUNK_DOWN_COUNTER';

// Action Creator
export const increment = () => ({
  type: INCREMENT_COUNTER,
});

export const decrement = () => ({
  type: DECREMENT_COUNTER,
});

export const sagaIncrement = () => ({
  type: SAGA_UP,
  value: 1,
});

export const sagaDecrement = () => ({
  type: SAGA_DOWN,
  value: 1,
});

export const thunkIncrement = () => ({
  type: THUNK_UP_COUNTER,
  value: 3,
});

export const thunkDecrement = () => ({
  type: THUNK_DOWN_COUNTER,
  value: 3,
});

export const thunkIncrementAsync = () => (dispatch) => {
  setTimeout(() => {
    // 1 초뒤 dispatch 합니다
    dispatch(thunkIncrement());
  }, 1000);
};

export const thunkDecrementAsync = () => (dispatch) => {
  setTimeout(() => {
    // 1 초뒤 dispatch 합니다
    dispatch(thunkDecrement());
  }, 1000);
};

export const initialState = {
  value: 1,
  sagaValue: 1,
  thunkValue: 1,
};

const sagaCounterReducer = (state = initialState, action) => {
  const newState = { ...state };
  switch (action.type) {
    case SAGA_UP_ASYNC:
      newState.sagaValue += action.value;
      break;
    case SAGA_DOWN_ASYNC:
      newState.sagaValue -= action.value;
      break;
    default:
      return newState;
  }
  return newState;
};

const thunkCounterReducer = (state = initialState, action) => {
  switch (action.type) {
    case THUNK_UP_COUNTER:
      return { ...state, thunkValue: state.thunkValue + action.value };
    case THUNK_DOWN_COUNTER:
      return { ...state, thunkValue: state.thunkValue - action.value };
    default:
      return { ...state };
  }
};

const rootReducer = combineReducers({
  counter: counterReducer,
  sagaCounter: sagaCounterReducer,
  thunkCounter: thunkCounterReducer,
});

export default rootReducer;
