import { combineReducers } from 'redux';

// Action Types
export const INCREMENT_COUNTER = 'INCREMENT_COUNTER';
export const DECREMENT_COUNTER = 'DECREMENT_COUNTER';
export const AGE_UP = 'AGE_UP';
export const AGE_DOWN = 'AGE_DOWN';
export const AGE_UP_ASYNC = 'AGE_UP_ASYNC';
export const AGE_DOWN_ASYNC = 'AGE_DOWN_ASYNC';

// Action Creator
export const increment = () => ({
  type: INCREMENT_COUNTER,
});

export const decrement = () => ({
  type: DECREMENT_COUNTER,
});

export const ageIncrement = () => ({
  type: AGE_UP,
  value: 1,
});

export const ageDecrement = () => ({
  type: AGE_DOWN,
  value: 1,
});

export const initialState = {
  value: 1,
  age: 20,
};

const ageReducer = (state = initialState, action) => {
  const newState = { ...state };
  switch (action.type) {
    case AGE_UP_ASYNC:
      newState.age += action.value;
      break;
    case AGE_DOWN_ASYNC:
      newState.age -= action.value;
      break;
    default:
      return newState;
  }
  return newState;
};

const counterReducer = (state = initialState, action) => {
  switch (action.type) {
    case INCREMENT_COUNTER:
      return { ...state, value: state.value + 1 };
    case DECREMENT_COUNTER:
      return { ...state, value: state.value - 1 };
    default:
      return { ...state };
  }
};

const rootReducer = combineReducers({
  counter: counterReducer,
  age: ageReducer,
});

export default rootReducer;
