import React, {
  Fragment, useState, useEffect, memo,
} from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { FETCH_FOLLOW_COUNT_VALUE } from '../../redux/reducers/followReducer';

const Profile = memo(() => {
  const login = useSelector((state) => state.sagaLogin, []);
  const { following, follower } = useSelector((state) => state.follow, []);
  const dispatch = useDispatch();
  useEffect(() => {
    // console.log(follow)
    const fetchData = async () => {
      dispatch({ type: FETCH_FOLLOW_COUNT_VALUE, payload: login.username });
    };
    fetchData();
  }, [following, follower]);

  return (
    <Fragment>
      <p className="p_name">
        {`${login.name} (${login.username})`}
      </p>
      <div className="right_content">
        <table className="card">
          <thead>
            <tr>
              <th>Follower</th>
              <th>Following</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td>{follower}</td>
              <td>{following}</td>
            </tr>
          </tbody>
        </table>
      </div>
      <style jsx>
        {`
          .right_content{
            float:left;
            width:30%;
            height:100px;
            background-color:white;
            padding:10px;
          }
          .card_title{
            margin-bottom:10px;
          }
          .p_name{
            margin:10px auto;
            font-size:15px;
          }
          table{
            text-align:center;
          }
        `}
      </style>
    </Fragment>
  );
});

export default Profile;
