import { combineReducers } from 'redux';

// Action Types
export const INCREMENT_COUNTER = 'INCREMENT_COUNTER';
export const DECREMENT_COUNTER = 'DECREMENT_COUNTER';
export const SAGA_UP = 'SAGA_UP';
export const SAGA_DOWN = 'SAGA_DOWN';
export const SAGA_UP_ASYNC = 'SAGA_UP_ASYNC';
export const SAGA_DOWN_ASYNC = 'SAGA_DOWN_ASYNC';

// Action Creator
export const increment = () => ({
  type: INCREMENT_COUNTER,
});

export const decrement = () => ({
  type: DECREMENT_COUNTER,
});

export const sagaIncrement = () => ({
  type: SAGA_UP,
  value: 1,
});

export const sagaDecrement = () => ({
  type: SAGA_DOWN,
  value: 1,
});

export const initialState = {
  value: 1,
  age: 20,
};

const sagaCounterReducer = (state = initialState, action) => {
  const newState = { ...state };
  switch (action.type) {
    case SAGA_UP_ASYNC:
      newState.age += action.value;
      break;
    case SAGA_DOWN_ASYNC:
      newState.age -= action.value;
      break;
    default:
      return newState;
  }
  return newState;
};

const counterReducer = (state = initialState, action) => {
  switch (action.type) {
    case INCREMENT_COUNTER:
      return { ...state, value: state.value + 1 };
    case DECREMENT_COUNTER:
      return { ...state, value: state.value - 1 };
    default:
      return { ...state };
  }
};
const thunkCounterReducer = (state = initialState, action) => {
  switch (action.type) {
    case INCREMENT_COUNTER:
      return { ...state, value: state.value + 1 };
    case DECREMENT_COUNTER:
      return { ...state, value: state.value - 1 };
    default:
      return { ...state };
  }
};

const rootReducer = combineReducers({
  counter: counterReducer,
  sagaCounter: sagaCounterReducer,
  thunkCounter: thunkCounterReducer,
});

export default rootReducer;
