/* eslint-disable no-nested-ternary */
import React, {
  Fragment, useState,
  useEffect, memo,
} from 'react';
import axios from 'axios';
import Router from 'next/router';
import { useDispatch, useSelector } from 'react-redux';
import Login from '../components/login/Login';
import Main from '../components/main/Main';
import { SESSION_CHECK } from '../redux/reducers/sagaLoginReducer';

const Index = memo((props) => {
  const login = useSelector((state) => state.sagaLogin, []);
  const dispatch = useDispatch();
  const { loginStatus } = login;

  useEffect(() => {
    const fetchData = () => {
      dispatch({ type: SESSION_CHECK });
    };
    fetchData();
  }, [loginStatus]);
  const Foo = () => {
    if (loginStatus === undefined || loginStatus === 'ongoing') {
      return <img src="../static/img/page_loading.png" width="1100px" alt="" />;
    }
    return loginStatus !== 'failed' ? <Main /> : <Login />;
  };
  return (
    <Fragment>
      {Foo()}
    </Fragment>
  );
});
Index.getInitialProps = () => {

};
export default Index;
