/* eslint-disable jsx-a11y/label-has-associated-control */
/* eslint-disable jsx-a11y/anchor-is-valid */
import React, { useState, useRef, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import Link from 'next/link';
import { MDCTextField } from '@material/textfield';
import { Button, InputGroup, FormControl } from 'react-bootstrap';
import { thunkIncrementAsync } from '../redux/reducers/loginReducer';


const Login = (props) => {
  const login = useSelector((state) => state.login, []);
  useEffect(() => {
    console.log(login);
  }, [login]);
  const dispatch = useDispatch();
  const [id, setId] = useState('');
  const [password, setPassword] = useState('');
  const idInput = useRef();
  const pwInput = useRef();

  const loginSubmitForm = (e) => {
    e.preventDefault();
    dispatch(thunkIncrementAsync(id, password));
  };

  const textField = new MDCTextField(document.querySelector('.mdc-text-field'));
  return (
    <div className="loginForm">
      <h1>SNSOL 로그인</h1>
      <label className="mdc-text-field">
        <div className="mdc-text-field__ripple" />
        <input className="mdc-text-field__input" type="text" aria-labelledby="my-label-id" />
        <span className="mdc-floating-label" id="my-label-id">Hint text</span>
        <div className="mdc-line-ripple" />
      </label>
      <form onSubmit={loginSubmitForm}>
        <div>
          <input type="text" ref={idInput} value={id} onChange={(e) => setId(e.target.value)} id="uid" />
        </div>
        <div>
          <input type="password" ref={pwInput} autoComplete="on" value={password} onChange={(e) => setPassword(e.target.value)} />
        </div>
        <Button size="lg" block type="submit">로그인</Button>
        <Button size="lg" variant="warning" block type="button">KaKao 로그인</Button>
      </form>
      <div className="login_etc">
        <div>
          <Link href="/">
            <a>회원가입</a>
          </Link>
        </div>
        <div>
          <Link href="/">
            <a>아이디 찾기</a>
          </Link>
          &nbsp;
          &
          &nbsp;
          <Link href="/">
            <a>비밀번호 찾기</a>
          </Link>
        </div>
      </div>
      <style jsx>
        {`
          .loginForm {
            width:350px;
            margin: 0 auto;
            padding:50px 0 150px 0;
          }
          h1{
            letter-spacing:1px;
            font-size:30px;
            font-weight:500;
            text-align:center;
            margin:20px 0;
          }
          .loginForm input{
            padding:10px;
          }
          .loginForm input{
            width:100%;
            height:50px;
            margin:5px 0px;
            border-radius: 5px;
            border: 1px solid #BDBDBD
          }
          .login_etc {
            margin-top:10px;
            font-size:12px;
          }
          .login_etc div:first-child{
            float:left;
          }
          .login_etc div:last-child{
            float:right;
          }
        `}
      </style>
    </div>
  );
};

export default Login;
