import React, { Fragment, useState, useEffect } from 'react';
import axios from 'axios';
import Router from 'next/router';
import { useDispatch, useSelector } from 'react-redux';
import Login from '../components/login/Login';
import Main from '../components/main/Main';
import { SESSION_CHECK } from '../redux/reducers/sagaLoginReducer';

const Index = (props) => {
  const login = useSelector((state) => state.sagaLogin, []);
  const dispatch = useDispatch();
  const { loginStatus } = login;

  useEffect(() => {
    const fetchData = () => {
      console.log(loginStatus);
      dispatch({ type: SESSION_CHECK });
    };
    fetchData();
  }, [loginStatus]);
  const foo = () => {
    console.log('aaa');
  };
  const Render = () => {
    if (loginStatus === undefined || loginStatus === 'ongoing') {
      return <div />;
    }
    return loginStatus !== 'failed' ? <Main /> : <Login />;
  };
  return (
    <Fragment>
      <Render />
    </Fragment>
  );
};
Index.getInitialProps = () => {

};
export default Index;
