import React, { Fragment, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { FETCH_CARDS_START } from '../../redux/reducers/cardReducer';

const Card = () => {
  const login = useSelector((state) => state.sagaLogin, []);
  const { fetchState, card } = useSelector((state) => state.card, []);
  const dispatch = useDispatch();
  useEffect(() => {
    const fetchData = () => {
      dispatch({ type: FETCH_CARDS_START, payload: login.username });
    };
    fetchData();
  }, [fetchState]);
  // console.log(card);
  return (
    <Fragment>
      <div className="left_content">
        {card.card.map((v, i) => (
          <div className="card main_card" key={i}>
            <div>
              <span>{v.title}</span>
            </div>
            <div>
              {v.desc}
            </div>
          </div>
        ))}
      </div>
      <style jsx>
        {`
          .left_content{
            float:left;
            width:69%;
            margin-right: 1%;
          }
          .main_card{
            margin:10px 0;
            padding:10px;
          }
        `}
      </style>
    </Fragment>
  );
};

export default Card;
