import React, { Fragment, useEffect } from 'react';
import axios from 'axios';
import { useDispatch, useSelector } from 'react-redux';
import { increment, decrement } from '../redux/reducers/counterReducer';
import { sagaIncrement, sagaDecrement } from '../redux/reducers/sagaCounterReducer';
import { thunkIncrementAsync, thunkDecrementAsync } from '../redux/reducers/thunkCounterReducer';

const Index = () => {
  // const { value } = useSelector((state) => state.counter, []);
  // const { sagaValue } = useSelector((state) => state.sagaCounter, []);
  // const { thunkValue } = useSelector((state) => state.thunkCounter, []);

  const { login } = useSelector((state) => state.login, []);
  console.log(login);
  // useEffect(() => {
  //   const fetchData = async () => {
  //     const result = await axios.get('http://127.0.0.1:3001/auth/session_check');
  //     // setData(result.data);
  //   };
  //   fetchData();
  // }, []);
  const foo = () => {
    console.log('aaa');
  };
  return (
    <Fragment>
      <div id="content">
        <div className="left_content">
          <div className="card">
            <div>
              <span>프로필사진</span>
              <span>이름</span>
            </div>
            <div>
              이미지슬라이드
            </div>
            <div>
              <button onClick={foo} onKeyDown={foo} type="button">
                <span>좋아요</span>
                <i className="fa fa-heart-o" aria-hidden="true" />
              </button>
              <span>댓글</span>
            </div>
          </div>
        </div>
        <div className="right_content">profile</div>
      </div>
      <style jsx>
        {`
          #content{
            margin: 10px 0;
            overflow:hidden;
          }
          .left_content{
            float:left;
            width:69%;
            margin-right: 1%;
          }
          .right_content{
            float:left;
            width:30%;
            height:500px;  
            background-color:blue;
          }
          .card{
            color:black;
          }
        `}
      </style>
    </Fragment>
  );
};
Index.getInitialProps = async (context) => {
  // console.log(context);
  const res = await axios.get('http://127.0.0.1:3001/auth/session_check');
  // console.log(res);
};

export default Index;
